from pydoc import HTMLDoc
import challrepo
import result

def writedocs():
    """ Writes html documentation for the challrepo and result modules to 
    html files. Requires a 'docs' directory in the same directory as the script
    """
    
    htmldoc = HTMLDoc()
    
    # removed output for these classes as they are included in module 'challrepo'
    
    # f = open('resultclass.html', 'w')
    # f.write(htmldoc.docclass(challrepo.Result))
    # f.close()
    
    # f = open('challenge.html', 'w')
    # f.write(htmldoc.docclass(challrepo.Challenge))
    # f.close()
    
    # f = open('challengerepo.html', 'w')
    # f.write(htmldoc.docclass(challrepo.ChallengeRepo))
    # f.close()
    
    #challrepo.ensure_dir('./docs')
    
    f = open('docs/challrepo.html', 'w')
    f.write(htmldoc.docmodule(challrepo))
    f.close()
    
    f = open('docs/result.html', 'w')
    f.write(htmldoc.docmodule(result))
    f.close()

if __name__ == "__main__":
    writedocs()