
"""provides common result comparison functions for use by activity developers"""

def equals_success_func(target, result):
    """ Returns true if there is a result and it is the same as the target """
    if result is None:
        return False
    return target == result

def not_equals_success_func(target, result):
    """ Returns true if there is a result and it is not the same as the target """
    if result is None:
        return False
    return target != result

def ge_success_func(target, result):
    """ Returns true if result is not None and is at least as high as target """
    if result is None:
        return False
    return result >= target

def le_success_func(target, result):
    """Returns true if result is not None and is less than or equal to target"""
    if result is None:
        return False
    return result <= target
    
def gt_success_func(target, result):
    """ Returns true if result is not None and is higher than target """
    if result is None:
        return False
    return result > target

def lt_success_func(target, result):
    """Returns true if result is not None and is less than target"""
    if result is None:
        return False
    return result < target

