import gtk

from glycogen.desktop.activitybox import ActivityBox


class PathwayView(gtk.VBox):
    # for now, this just contains an activity box.
    
    def __init__(self, **kwargs):
        # just make an activity box and put it in here
        gtk.VBox.__init__(self, False, 0)
        self.activity_box = ActivityBox(**kwargs)
        self.pack_start(self.activity_box, True, False, 0)
        self.activity_box.show()
        self.pathway_display = PathwayDisplay()
        self.pack_start(self.pathway_display, True, True, 0)
        self.pathway_display.show()
        self.show()

class PathwayDisplay(gtk.Button):

    def __init__(self):
        gtk.Button.__init__(self, "Pathway display")
        
